import React, { useEffect, useState } from "react";
import "./EventosPage.css"
import Title from "../../components/Title/Title"
import MainContent from "../../components/MainContent/MainContent";
import Container from "../../components/Container/Container";
import ImageIlustrator from "../../components/ImageIllustrator/ImageIllustrator";
import eventoImage from "../../assets/images/evento.svg";
import { Input, Button, Select } from "../../components/FormComponents/FormComponents";


const EventosPage = () => {



    return (
        <>

            <MainContent>
                <section className="cadastro-evento-section">
                    <Container>
                        <div className="cadastro-evento__box">
                            <Title titleText="Cadastro de Evento" />
                            <ImageIlustrator imageRender={eventoImage} />

                            <form className="form-evento" onSubmit={frmEdit ? handleUpdate : handleSubmit}>
                                {
                                !frmEdit ?
                                (
                                <>
                                <Input
                                    id="Nome"
                                    type={"text"}
                                    placeholdder={"Nome"}
                                    name={"nome"}
                                    required={"required"}
                                    value={nomeEvento}
                                    manipulationFunction={(e) => {
                                        setNomeEvento(e.target.value)
                                    }}
                                />

                                <Button
                                    textButton="Cadastrar"
                                    id="cadastrar"
                                    name="cadastrar"
                                    //formulário só será chamado pois seu type é submit
                                    type="submit"
                                />
                                </>
                                ):
                                <></>}

                            </form>
                        </div>
                    </Container>
                </section>
            </MainContent>
        </>
    );
};

export default EventosPage;
