import React from "react";
import"./TableEventos.css";

import editPen from "../../../assets/images/edit-pen.svg";
import trashDelete from "../../../assets/images/trash-delete.svg";

const TableEventos = ({dados,fnDelete = null, fnUpdate = null}) => {
    return (

        <table className="table-data">
            {/* cabe√ßalho */}
            <thead className="table-data__head">
                <tr className="table-data__head-row">
                    <th className="table-data__head-title table-data__head-title--big">Nome</th>
                    <th className="table-data__head-title table-data__head-title--big">Descricao</th>
                    <th className="table-data__head-title table-data__head-title--big">Tipo de Evento</th>
                    <th className="table-data__head-title table-data__head-title--big">Data</th>
                    <th className="table-data__head-title table-data__head-title--little">Editar</th>
                    <th className="table-data__head-title table-data__head-title--little">Deletar</th>
                </tr>
            </thead>

             {/* corpo */}
             <tbody>
                {dados.map((Eventos) => {
                    return (
                        <tr className="table-data__head-row" key={Eventos.idEvento}>
                            <td className="table-data__data table-data__data--big">
                                {Eventos.nomeEvento}
                            </td>
                            <td className="table-data__data table-data__data--big">
                                {Eventos.descricao}
                            </td>
                            <td className="table-data__data table-data__data--big">
                                {Eventos.tiposEvento.titulo}
                            </td>
                            <td className="table-data__data table-data__data--big">
                                {dateFormatDbToView(Eventos.dataEvento)}
                            </td>

                            <td className="table-data__data table-data__data--little">
                                <img className="table-data__icon" src={editPen} alt="" onClick={() =>fnUpdate(Eventos.idEvento)}/>
                            </td>

                            <td className="table-data__data table-data__data--little"
                            >
                                <img className="table-data__icon" src={trashDelete} alt="" onClick={(e) => {fnDelete(Eventos.idEvento)}}/>
                            </td>
                        </tr>
                    )
                })}

            </tbody>
        </table>
    );
};

export default TableEventos;
