import React from "react";
import "./EventosPage.css"
import Title from "../../components/Title/Title"
import MainContent from "../../components/MainContent/MainContent";
import Container from "../../components/Container/Container";
import ImageIlustrator from "../../components/ImageIllustrator/ImageIllustrator";
import eventoImage from "../../assets/images/evento.svg";
import { Input, Button} from "../../components/FormComponents/FormComponents";
import TableEventos from "./TableEventos/TableEventos";


const EventosPage = () => {



    return (

      <>
        <MainContent>
              <section className="cadastro-evento-section">
                  
                      
                          <div className="cadastro-evento__box">
                              <Title titleText="Cadastro de Evento" />
                              <ImageIlustrator imageRender={eventoImage} />

                              <form className="form-evento">
          (
                                <>
                                <Input
                                    id="Nome"
                                    type={"text"}
                                    placeholdder={"Nome"}
                                    name={"nome"}
                                    required={"required"}
                                    value="Nome"
                                />

                                <Button
                                    textButton="Cadastrar"
                                    id="cadastrar"
                                    name="cadastrar"
                                />
                                </>
                                ):
                                <></>

                            </form>
                        </div>
                </section>
                
                <section className="lista-eventos-section">
                  <Container>
                    <Title titleText={"Lista Eventos"} color="white" />

                    <TableEventos
                      dados={tipoEventos}
                      fnUpdate={showUpdateForm}
                      fnDelete={handleDelete}
                    />
                  </Container>
                </section>
            </MainContent>
        </>
    );
};

export default EventosPage;
