import React, { useEffect, useState } from "react";
import './EventosPage.css';
import MainContent from "../../components/MainContent/MainContent";
import Container from "../../components/Container/Container";
import Titulo from "../../components/Title/Title"
import ImageIllustrator from "../../components/ImageIllustrator/ImageIllustrator";
import eventImage from "../../assets/images/evento.svg"
import {Button, Input, Select} from "../../components/FormComponents/FormComponents"
import api, { eventsResource, eventsTypeResource } from "../../Services/Service";
import TableEv from "./TableEventos/TableEv";

const EventosPage = () => {

    const idInstituicao = "34c6abb6-dec4-4fa9-91bd-c0b5ac7a39bf";
    const [eventos, setEventos] = useState([]);
    const [tipoEventos, setTipoEventos] = useState([]);
    const [nomeEvento, setNomeEvento] = useState("");
    const [descricao, setDescricao] = useState('');
    const [idTipoEvento, setIdTipoEvento] = useState('');
    const [idEvento, setIdEvento] = useState(null);
    const [dataEvento, setDataEvento] = useState('');
    async function loadEventsType() {

      try {
          const retorno = await api.get(eventsTypeResource);
          setTipoEventos(retorno.data)
      }

      catch (error) {
          alert('erro ')
      }
  }

  async function loadEvents() {

      try {
          const retorno = await api.get(eventsResource);
          setEventos(retorno.data)
      }

      catch (error) {
          alert('erro')
      }
  }

  useEffect(() => {
      loadEventsType()
      loadEvents()
  }, [])


  async function handleSubmit(e) {
      e.preventDefault();
      if (nomeEvento.trim().length < 3) {
          alert('Nome do evento deve conter pelo menos 3 caracteres')
          return;
      }

      try {
          await api.post(eventsResource, {
              dataEvento: dataEvento,
              nomeEvento: nomeEvento,
              descricao: descricao,
              idTipoEvento: idTipoEvento,
              idInstituicao: IdInstituicao
          })

          loadEvents()

      } catch (error) {
          alert('erro');
      }
  }

  async function handleUpdate(e) {
      e.preventDefault();
      if (nomeEvento.trim().length < 3) {
          alert('Nome do evento deve conter pelo menos 3 caracteres')
          return;
      }
      try {
          const promise = await api.put(`${eventsResource}/${idEvento}`, {
              dataEvento: dataEvento,
              nomeEvento: nomeEvento,
              descricao: descricao,
              idTipoEvento: idTipoEvento,
          })
      } 
      catch (error) {

      }
  }

  function editActionAbort() { }

  async function showUpdateForm(idElement) {
      setFrmEdit(true)
      try {
          const promise = await api.get(`${eventsResource}/${idElement}`, { idElement })
          setDataEvento(promise.data.dataEvento)
          setNomeEvento(promise.data.nomeEvento)
          setDescricao(promise.data.descricao)
          setIdTipoEvento(promise.data.idTipoEvento)

      } catch (error) {
          alert('erro')
      }
  }

  async function handleDelete(idElement) {
      if (window.confirm('Confirma a exclusÃ£o')) {
          try {
              const promise = await api.delete(`${eventsResource}/${idElement}`, { idElement })
              if (promise.status == 204) {
                  const buscaEventos = await api.get(eventsResource);
                  setEventos(buscaEventos.data)
              }
          } catch (error) {
              alert('erro')
          }
      }
  }

  function tituloTipo(tipoEventos) {
      let arrayOptions = []

      tipoEventos.forEach(element => {
          arrayOptions.push({ value: element.idTipoEvento, text: element.titulo })
      })
      return arrayOptions
  }

    return(
    <MainContent>

        <section className="cadastro-evento-section">
            <Container>

                <div className="cadastro-evento__box">
                    <Titulo titleText={"Pagina de Eventos"}/>

                    <ImageIllustrator imageRender={eventImage}/>
                    <form action="" className="ftipo-evento">
                        <Input 
                        id="nomeEvento"
                        type= "text"
                        name="nomeEvento"
                        placeholder="Nome"
                        required="required"
                        />

                        <Input 
                          id="descricao"
                          type="text"
                          name="descricao"
                          placeholder="DescriÃ§Ã£o"
                          required="required"
                        />

                        <Input 
                        id="data"
                        type={'date'}
                        name="data"
                        placeholder="dd/mm/aa"
                        required="required"
                        />

                        <Select
                            id='TiposEvento'
                            name={'tiposEvento'}
                            required={'required'}
                            options={tituloTipo(tipoEventos)}
                        />

                        <Button 
                            textButton="Cadastrar"
                            id="cadastrar"
                            name="cadastrar"
                            type="submit"
                        />
                       
                    </form>
                </div>
            </Container>
        </section>
        <section className="lista-eventos-section">
            <Container>
                <Titulo titleText={"Lista de Eventos"} color={"white"}/>

                <TableEv 
                  dados={eventos}
                  fnDelete={handleDelete}
                />
            </Container>
        </section>
    </MainContent>
    );
};

export default EventosPage;
