import React from "react";
import "./EventosPage.css";
import Title from '../../components/Title/Title';
import MainContent from "../../components/MainContent/MainContent";
import Notification from "../../components/Notification/Notification";
import Banner from "../../components/Banner/Banner";
import Container from "../../components/Container/Container";
import NextEvent from "../../components/NextEvent/NextEvent";

const EventosPage = () => {
  
  return (
    <>
        <MainContent>
            <section className="cadastro-evento-section">
                <Container>
                    <div className="cadastro-evento__box">
                        <Title titleText="Cadastro de Evento" />
                        <ImageIlustrator imageRender={eventoImage} />

                        <form className='f-evento' onSubmit={frmEdit ? handleUpdate : handleSubmit}>
                            {
                            !frmEdit ?
                            (
                            <>
                            <Input
                                id='Nome'
                                type={'text'}
                                placeholdder={'Nome'}
                                name={'nome'}
                                required={'required'}
                                value={nomeEvento}
                                manipulationFunction={(e) => {
                                    setNomeEvento(e.target.value)
                                }}
                            />

                            <Input
                                id='Descricao'
                                type={'text'}
                                placeholdder={'Descrição'}
                                name={'descricao'}
                                required={'required'}
                                value={descricao}
                                manipulationFunction={(e) => {
                                    setDescricao(e.target.value)
                                }}
                            />

                            <Select
                                id='TiposEvento'
                                name={'tiposEvento'}
                                required={'required'}
                                options={tituloTipo(tiposEvento)}
                                value={idTipoEvento}
                                manipulationFunction={(e) =>{
                                    setIdTipoEvento(e.target.value)
                                }}
                            />

                            <Input
                                id='dataEvento'
                                type={'date'}
                                placeholdder={'dd/mm/aaaa'}
                                name={'Data'}
                                required={'required'}
                                value={dataEvento}
                                manipulationFunction={(e) => {
                                    setDataEvento(e.target.value)
                                }}
                            />

                            <Button
                                textButton="Cadastrar"
                                id="cadastrar"
                                name="cadastrar"
                                //formulário só será chamado pois seu type é submit
                                type="submit"
                            />
                            </>
                            ):
                            <></>}

                        </form>
                    </div>

                </Container>
            </section>

            <section className='lista-eventos-section'>
            <Container>
                    <Title titleText={"Lista Tipo de Eventos"} color="white"></Title>
                    <TableEvent
                        dados={eventos}
                        fnUpdate={showUpdateForm}
                        fnDelete={handleDelete}
                    />
                </Container>
            </section>
        </MainContent>
    </>
  );
  };

export default EventosPage;
